// healthtracker.js
import React, { useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { HeartPulse, Activity, Stethoscope } from "lucide-react";

export default function HealthTracker() {
  const [bpm, setBpm] = useState(72);
  const [isMeasuring, setIsMeasuring] = useState(false);
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [age, setAge] = useState("");
  const [apHi, setApHi] = useState("");
  const [apLo, setApLo] = useState("");
  const [smoke, setSmoke] = useState(0);
  const [alco, setAlco] = useState(0);
  const [cholesterol, setCholesterol] = useState("");
  const [gluc, setGluc] = useState("");
  const [gender, setGender] = useState(1);
  const [bmi, setBmi] = useState(null);
  const [status, setStatus] = useState("");
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);

  // Heartbeat simulation
  useEffect(() => {
    let interval;
    if (isMeasuring) {
      interval = setInterval(() => {
        const randomBpm = Math.floor(60 + Math.random() * 60);
        setBpm(randomBpm);
      }, 2000);
    }
    return () => clearInterval(interval);
  }, [isMeasuring]);

  // BMI calculator
  const calculateBMI = () => {
    if (height && weight) {
      const hMeters = height / 100;
      const bmiValue = (weight / (hMeters * hMeters)).toFixed(2);
      setBmi(bmiValue);

      if (bmiValue < 18.5) setStatus("Underweight");
      else if (bmiValue >= 18.5 && bmiValue < 25) setStatus("Normal");
      else if (bmiValue >= 25 && bmiValue < 30) setStatus("Overweight");
      else setStatus("Obese");
    }
  };

  // Send data to Flask backend
  const handlePredict = async () => {
    setLoading(true);
    setPrediction(null);

    const payload = {
      date: new Date().toISOString().split("T")[0],
      country: "India",
      id: 1,
      active: 1,
      age: Number(age),
      alco: Number(alco),
      ap_hi: Number(apHi),
      ap_lo: Number(apLo),
      cholesterol: Number(cholesterol),
      gender: Number(gender),
      gluc: Number(gluc),
      height: Number(height),
      occupation: "Engineer",
      smoke: Number(smoke),
      weight: Number(weight),
    };

    try {
      const response = await fetch("http://127.0.0.1:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const result = await response.json();
      setPrediction(result.message);
    } catch (error) {
      setPrediction("Error: Could not connect to backend");
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-6 text-blue-700">
        Interactive Health Tracker
      </h1>

      {/* Heartbeat Monitor */}
      <Card className="w-full max-w-md mb-6 shadow-lg">
        <CardContent className="p-6 text-center">
          <HeartPulse className="mx-auto mb-4 text-red-500" size={48} />
          <h2 className="text-xl font-semibold mb-2">Heartbeat Monitor</h2>
          <p className="text-2xl font-bold text-gray-800 mb-2">{bpm} BPM</p>
          <Button
            onClick={() => setIsMeasuring(!isMeasuring)}
            className="mt-4 w-full"
          >
            {isMeasuring ? "Stop Measuring" : "Start Measuring"}
          </Button>
        </CardContent>
      </Card>

      {/* BMI Calculator */}
      <Card className="w-full max-w-md mb-6 shadow-lg">
        <CardContent className="p-6">
          <Activity className="mx-auto mb-4 text-green-500" size={48} />
          <h2 className="text-xl font-semibold mb-4 text-center">
            BMI Calculator
          </h2>
          <input
            type="number"
            value={height}
            onChange={(e) => setHeight(e.target.value)}
            className="w-full border p-2 rounded mb-2"
            placeholder="Height (cm)"
          />
          <input
            type="number"
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
            className="w-full border p-2 rounded mb-2"
            placeholder="Weight (kg)"
          />
          <Button onClick={calculateBMI} className="w-full mb-4">
            Calculate BMI
          </Button>
          {bmi && (
            <div className="text-center">
              <p className="text-lg font-bold">BMI: {bmi}</p>
              <p
                className={`text-sm font-semibold ${
                  status === "Normal"
                    ? "text-green-600"
                    : status === "Underweight"
                    ? "text-yellow-600"
                    : "text-red-600"
                }`}
              >
                Status: {status}
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Disease Prediction */}
      <Card className="w-full max-w-md shadow-lg">
        <CardContent className="p-6">
          <Stethoscope className="mx-auto mb-4 text-blue-500" size={48} />
          <h2 className="text-xl font-semibold mb-4 text-center">
            Disease Risk Prediction
          </h2>
          <div className="grid grid-cols-2 gap-2 mb-4">
            <input
              type="number"
              placeholder="Age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              className="border p-2 rounded"
            />
            <input
              type="number"
              placeholder="Systolic BP (ap_hi)"
              value={apHi}
              onChange={(e) => setApHi(e.target.value)}
              className="border p-2 rounded"
            />
            <input
              type="number"
              placeholder="Diastolic BP (ap_lo)"
              value={apLo}
              onChange={(e) => setApLo(e.target.value)}
              className="border p-2 rounded"
            />
            <input
              type="number"
              placeholder="Cholesterol"
              value={cholesterol}
              onChange={(e) => setCholesterol(e.target.value)}
              className="border p-2 rounded"
            />
            <input
              type="number"
              placeholder="Glucose"
              value={gluc}
              onChange={(e) => setGluc(e.target.value)}
              className="border p-2 rounded"
            />
            <select
              value={gender}
              onChange={(e) => setGender(e.target.value)}
              className="border p-2 rounded"
            >
              <option value={1}>Male</option>
              <option value={0}>Female</option>
            </select>
            <select
              value={smoke}
              onChange={(e) => setSmoke(e.target.value)}
              className="border p-2 rounded"
            >
              <option value={0}>Non-Smoker</option>
              <option value={1}>Smoker</option>
            </select>
            <select
              value={alco}
              onChange={(e) => setAlco(e.target.value)}
              className="border p-2 rounded"
            >
              <option value={0}>No Alcohol</option>
              <option value={1}>Alcohol User</option>
            </select>
          </div>
          <Button onClick={handlePredict} className="w-full">
            {loading ? "Predicting..." : "Check Risk"}
          </Button>
          {prediction && (
            <p className="text-center mt-4 font-bold text-lg">{prediction}</p>
          )}
        </CardContent>
      </Card>
    </div>
  );
}
