import React from "react";

function Lifestyle() {
  const tips = [
    { title: "Stay Active", desc: "Engage in at least 30 minutes of exercise daily to maintain energy and health." },
    { title: "Balanced Diet", desc: "Include fresh fruits, vegetables, lean proteins, and whole grains in your meals." },
    { title: "Quality Sleep", desc: "Aim for 7â€“8 hours of uninterrupted sleep to allow your body to recover." },
    { title: "Mindfulness", desc: "Practice meditation, journaling, or deep breathing to reduce stress." },
  ];

  return (
    <div className="page">
      <h1 className="page-title">Healthy Lifestyle</h1>
      <p className="page-subtitle">Simple habits for a happier, healthier you ðŸŒ±</p>

      <div className="cards">
        {tips.map((tip, index) => (
          <div key={index} className="card">
            <h2>{tip.title}</h2>
            <p>{tip.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Lifestyle;
