import React, { useState, useEffect } from "react";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
  BarChart, Bar, RadialBarChart, RadialBar
} from "recharts";

export default function HealthTracker() {
  const [bpm, setBpm] = useState(72);
  const [isMeasuring, setIsMeasuring] = useState(false);
  const [heartbeatData, setHeartbeatData] = useState([]);
  const [bmiData, setBmiData] = useState({ weight: "", height: "", age: "", gender: "male", activity: "sedentary" });
  const [bmiResult, setBmiResult] = useState(null);
  const [bmiHistory, setBmiHistory] = useState([]);
  const [vitals, setVitals] = useState({ systolic: "", diastolic: "", sugar: "", cholesterol: "" });
  const [prediction, setPrediction] = useState(null);

  // Simulate heartbeat
  useEffect(() => {
    let interval;
    if (isMeasuring) {
      interval = setInterval(() => {
        const newBpm = Math.floor(60 + Math.random() * 40);
        setBpm(newBpm);
        setHeartbeatData((prev) => [
          ...prev.slice(-14),
          { time: new Date().toLocaleTimeString().slice(3, 8), bpm: newBpm },
        ]);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isMeasuring]);

  // Calculate BMI
  const calculateBMI = () => {
    const { weight, height, age, gender, activity } = bmiData;
    if (!weight || !height) return;
    const hM = height / 100;
    const bmi = (weight / (hM * hM)).toFixed(2);

    let category = "Normal";
    if (bmi < 18.5) category = "Underweight";
    else if (bmi >= 25 && bmi < 30) category = "Overweight";
    else if (bmi >= 30) category = "Obese";

    const result = { bmi, category, age, gender, activity };
    setBmiResult(result);
    setBmiHistory((prev) => [...prev, { name: `Check ${prev.length + 1}`, bmi: parseFloat(bmi) }]);
  };

  // Predict heart disease (backend call)
  const predictDisease = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          date: "2025-09-07",
          country: "India",
          id: 1,
          active: 1,
          age: bmiData.age || 30,
          alco: 0,
          ap_hi: vitals.systolic || 120,
          ap_lo: vitals.diastolic || 80,
          cholesterol: vitals.cholesterol || 180,
          gender: bmiData.gender === "male" ? 1 : 2,
          gluc: vitals.sugar || 1,
          height: bmiData.height,
          occupation: "Engineer",
          smoke: 0,
          weight: bmiData.weight,
        }),
      });
      const data = await response.json();
      setPrediction(data.message);
    } catch (error) {
      console.error(error);
      setPrediction("Error connecting to backend.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-200 p-8">
      {/* Header */}
      <header className="mb-10 text-center">
        <h1 className="text-4xl font-bold text-blue-800">üè• Health Tracker Dashboard</h1>
        <p className="text-lg text-gray-700">Track your vitals, BMI, and heart health with AI predictions</p>
      </header>

      <div className="grid lg:grid-cols-2 gap-8">
        {/* Heartbeat Monitor */}
        <div className="bg-white p-6 rounded-2xl shadow-xl">
          <h2 className="text-2xl font-semibold text-blue-600 mb-4">üíì Heartbeat Monitor</h2>
          <p className="text-5xl font-bold text-gray-900 mb-4">{bpm} BPM</p>
          <button
            onClick={() => setIsMeasuring(!isMeasuring)}
            className="bg-blue-600 hover:bg-blue-700 text-white w-full py-3 rounded-xl transition"
          >
            {isMeasuring ? "Stop Measuring" : "Start Measuring"}
          </button>

          {/* Heartbeat Chart */}
          <div className="mt-6 h-56">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={heartbeatData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="time" />
                <YAxis domain={[50, 120]} />
                <Tooltip />
                <Line type="monotone" dataKey="bpm" stroke="#2563eb" strokeWidth={3} dot={false} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* BMI Calculator */}
        <div className="bg-white p-6 rounded-2xl shadow-xl">
          <h2 className="text-2xl font-semibold text-green-600 mb-4">‚öñÔ∏è BMI Calculator</h2>
          <div className="grid grid-cols-2 gap-4">
            <input type="number" placeholder="Weight (kg)" className="border p-2 rounded-lg"
              onChange={(e) => setBmiData({ ...bmiData, weight: e.target.value })} />
            <input type="number" placeholder="Height (cm)" className="border p-2 rounded-lg"
              onChange={(e) => setBmiData({ ...bmiData, height: e.target.value })} />
            <input type="number" placeholder="Age" className="border p-2 rounded-lg"
              onChange={(e) => setBmiData({ ...bmiData, age: e.target.value })} />
            <select className="border p-2 rounded-lg" onChange={(e) => setBmiData({ ...bmiData, gender: e.target.value })}>
              <option value="male">Male</option><option value="female">Female</option>
            </select>
            <select className="col-span-2 border p-2 rounded-lg" onChange={(e) => setBmiData({ ...bmiData, activity: e.target.value })}>
              <option value="sedentary">Sedentary</option>
              <option value="active">Active</option>
              <option value="very-active">Very Active</option>
            </select>
          </div>
          <button onClick={calculateBMI} className="bg-green-600 hover:bg-green-700 text-white w-full py-3 mt-4 rounded-xl transition">Calculate BMI</button>

          {bmiResult && (
            <div className="mt-6 grid grid-cols-2 gap-4 items-center">
              <div>
                <p className="text-xl font-bold text-gray-800">BMI: {bmiResult.bmi}</p>
                <p className="text-lg text-gray-600">Category: {bmiResult.category}</p>
              </div>
              {/* BMI Gauge */}
              <ResponsiveContainer width="100%" height={120}>
                <RadialBarChart
                  innerRadius="70%" outerRadius="100%"
                  data={[{ name: "BMI", value: bmiResult.bmi, fill: "#16a34a" }]}
                  startAngle={180} endAngle={0}
                >
                  <RadialBar minAngle={15} background clockWise dataKey="value" />
                </RadialBarChart>
              </ResponsiveContainer>
            </div>
          )}

          {/* BMI History Chart */}
          {bmiHistory.length > 0 && (
            <div className="mt-6 h-48">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={bmiHistory}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="bmi" fill="#16a34a" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          )}
        </div>

        {/* Vitals Tracker */}
        <div className="bg-white p-6 rounded-2xl shadow-xl">
          <h2 className="text-2xl font-semibold text-purple-600 mb-4">ü©∫ Vitals Tracker</h2>
          <div className="grid grid-cols-2 gap-4">
            <input type="number" placeholder="Systolic BP" className="border p-2 rounded-lg"
              onChange={(e) => setVitals({ ...vitals, systolic: e.target.value })} />
            <input type="number" placeholder="Diastolic BP" className="border p-2 rounded-lg"
              onChange={(e) => setVitals({ ...vitals, diastolic: e.target.value })} />
            <input type="number" placeholder="Sugar Level" className="border p-2 rounded-lg"
              onChange={(e) => setVitals({ ...vitals, sugar: e.target.value })} />
            <input type="number" placeholder="Cholesterol" className="border p-2 rounded-lg"
              onChange={(e) => setVitals({ ...vitals, cholesterol: e.target.value })} />
          </div>

          {/* Progress Bars */}
          <div className="mt-6 space-y-4">
            {vitals.systolic && (
              <div>
                <p className="text-sm text-gray-600">Blood Pressure: {vitals.systolic}/{vitals.diastolic}</p>
                <div className="w-full bg-gray-200 rounded-full h-3">
                  <div className="bg-purple-600 h-3 rounded-full" style={{ width: `${Math.min(vitals.systolic / 2, 100)}%` }}></div>
                </div>
              </div>
            )}
            {vitals.sugar && (
              <div>
                <p className="text-sm text-gray-600">Sugar Level: {vitals.sugar}</p>
                <div className="w-full bg-gray-200 rounded-full h-3">
                  <div className="bg-yellow-500 h-3 rounded-full" style={{ width: `${Math.min(vitals.sugar / 2, 100)}%` }}></div>
                </div>
              </div>
            )}
            {vitals.cholesterol && (
              <div>
                <p className="text-sm text-gray-600">Cholesterol: {vitals.cholesterol}</p>
                <div className="w-full bg-gray-200 rounded-full h-3">
                  <div className="bg-red-500 h-3 rounded-full" style={{ width: `${Math.min(vitals.cholesterol / 3, 100)}%` }}></div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Prediction */}
        <div className="bg-white p-6 rounded-2xl shadow-xl">
          <h2 className="text-2xl font-semibold text-red-600 mb-4">üß™ Heart Disease Prediction</h2>
          <button onClick={predictDisease} className="bg-red-600 hover:bg-red-700 text-white w-full py-3 rounded-xl transition">Run Prediction</button>
          {prediction && (
            <p className="mt-4 text-lg font-bold text-gray-800">{prediction}</p>
          )}
        </div>
      </div>
    </div>
  );
}
